/* 响应式设计 */
@media (max-width: 992px) {
    .about-preview .container {
        grid-template-columns: 1fr;
    }

    .about-image {
        order: -1;
        max-width: 600px;
        margin: 0 auto 30px;
    }

    .about-content {
        padding-right: 0;
        text-align: center;
    }

    .about-content .section-title {
        text-align: center;
    }

    .about-content .section-title::after {
        margin: 10px auto;
    }

    .about-content .btn {
        margin: 0 5px 10px;
    }
}

@media (max-width: 768px) {
    header .container {
        flex-wrap: wrap;
        justify-content: space-between;
    }

    .menu-toggle {
        display: block;
    }

    .logo {
        flex-direction:  ;
        align-items: center;
        width: calc(100% - 40px); /* 为菜单图标留出空间 */
    }

    .logo h1 {
        font-size: 1.2rem;
    }

    .logo-image {
        height: 40px;
    }

    nav {
        position: static; /* 取消绝对定位 */
        width: auto;      /* 取消固定宽度 */
        max-height: none; /* 取消高度限制 */
        overflow: visible; /* 取消隐藏 */
        display: flex;    /* 横向排列关键属性 */
        background-color: transparent; /* 可选：移除背景色 */
        box-shadow: none; /* 可选：移除阴影 */
    }

    nav ul {
        display: flex;    /* 横向排列子菜单 */
        flex-wrap: wrap;  /* 允许换行（如果项过多） */
        gap: 10px;        /* 控制菜单项间距 */
    }

    nav li {
        display: inline-block; /* 横向排列列表项 */
    }

    nav.active {
        max-height: 300px;
    }

    .hero h2 {
        font-size: 2.5rem;
    }

    /* 英雄区域响应式布局 */
    .hero .container {
        grid-template-columns: 1fr;
        gap: 30px;
    }

    .hero-content {
        text-align: center;
        order: 1;
    }

    .hero-image {
        order: 0;
    }

    .hero-image-container {
        max-width: 400px;
        margin: 0 auto;
    }

    .floating-code-1 {
        top: -10px;
        right: 10%;
    }

    .floating-code-2 {
        bottom: 5%;
        left: -15px;
    }
}

